mapper.update:
declare begin
insert into base_news_t( type, parse_bean, value, creation_date ) values ( #{it.type,jdbcType=VARCHAR}, #{it.parseBean,jdbcType=VARCHAR}, #{it.value,jdbcType=VARCHAR}, trunc(#{it.creationDate,jdbcType=DATE}) );
commit; end;

mapper.delete:
delete from base_news_t
where type=#{fo.type,jdbcType=VARCHAR}
		 and creation_date =trunc(#{fo.creationDate,jdbcType=DATE})
